import axios from 'axios';

const API_BASE = 'http://localhost:5000/contacts';

export const getContacts = () => axios.get(API_BASE);
export const createContact = (data) => axios.post(API_BASE, data);
export const updateContact = (id, data) => axios.put(`${API_BASE}/${id}`, data);
export const deleteContact = (id) => axios.delete(`${API_BASE}/${id}`);
